mapper.select:
select t.user_id, t.adress, t.symbol, t.amout,
t.occupied_amount, t.available_amount, t.type, t.create_by, t.create_time, t.update_by, t.update_time,
t.remark, t.search_value
from t_app_asset t
left join  t_app_user u on t.user_id=u.user_id



        AND find_in_set(#{adminParentIds},u.admin_parent_ids)

     and t.user_id = #{userId}
     and t.symbol = #{symbol}
     and t.adress = #{adress}
     and t.amout = #{amout}
     and t.type = #{type}
     and t.occupied_amount = #{occupiedAmount}
     and t.available_amount = #{availableAmount}
     and t.search_value = #{searchValue}


            and t.amout  >=  #{params.amountMin}


            and t.amout  <=  #{params.amountMax}


            and t.occupied_amount  >=  #{params.occupiedAmountMin}


            and t.occupied_amount  <=  #{params.occupiedAmountMax}


            and t.available_amount  >=  #{params.availableAmountMin}


            and t.available_amount  <=  #{params.availableAmountMax}


            and t.create_time  >=  #{params.startTime}


            and t.create_time  <=  #{params.endTime}



order by t.create_time desc

mapper.update:
update t_app_asset
adress = #{adress}, amout = #{amout}, occupied_amount = #{occupiedAmount}, available_amount = #{availableAmount}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, search_value = #{searchValue},
where user_id = #{userId} and symbol =#{symbol} and type=#{type}

update t_app_asset
available_amount = available_amount - #{money}, occupied_amount= occupied_amount + #{money}, update_time = now()
where user_id = #{userId} and symbol =#{symbol} and type=#{type}

update t_app_asset
amout = amout - #{money}, available_amount=available_amount-#{money}, update_time = now()
where user_id = #{userId} and symbol =#{symbol} and type=#{type}

update t_app_asset
amout = amout + #{money}, available_amount=available_amount + #{money}, update_time = now()
where user_id = #{userId} and symbol =#{symbol} and type=#{type}

update t_app_asset
available_amount = available_amount - #{money}, occupied_amount= occupied_amount + #{money}, update_time = now()
where user_id = #{userId} and symbol =#{symbol} and type=#{type}

update t_app_asset
amout = amout - #{money}, occupied_amount= occupied_amount - #{subtract}, available_amount= available_amount +#{availableAmount}, update_time = now()
where user_id = #{userId} and symbol =#{symbol} and type=#{type}
