mapper.select:


    and user_id = #{userId}
    and username like concat('%', #{username}, '%')
    and amount = #{amount}
    and bonus = #{bonus}
    and status = #{status}
    and serial_id = #{serialId}
    and tx_id = #{txId}
    and type = #{type}
    and search_value = #{searchValue}
    and address = #{address}
    and app_parent_ids = #{appParentIds}
    and admin_parent_ids = #{adminParentIds}
    and coin = #{coin}
    and to_address = #{toAddress}
    and block_time = #{blockTime}
    and host = #{host}
    and real_amount = #{realAmount}
    and file_name like concat('%', #{fileName}, '%')
    and recharge_remark = #{rechargeRemark}
    and notice_flag = #{noticeFlag}
    and order_type = #{orderType}

order by create_time desc

SELECT
a.id,
a.serial_id as serialId,
a.tx_id as txId,
a.user_id as userId,
a.username,
a.amount,
a.bonus,
a.remark,
a.type,
a.status,
a.update_time as updateTime,
a.update_by as updateBy,
a.create_time as createTime,
a.tree,
a.real_amount as realAmount,
a.block_time as blockTime,
a.address,
a.file_name as fileName,
a.`host`,
a.to_address as toAddress,
a.coin,
u.is_test as isTest,
a.recharge_remark as rechargeRemark,
a.order_type as orderType,
r.u_amount as uamount
FROM
t_app_recharge a
LEFT JOIN t_app_user u ON a.user_id = u.user_id
left join t_app_wallet_record r on r.serial_id = a.serial_id and (r.type = '1' or r.type = '3')


        AND a.remark is not null and a.remark !=''


        AND ( a.remark is null or a.remark ='' )


        AND a.id= #{id}


        AND a.user_id = #{userId}


        AND LOWER(a.address) = LOWER(#{address})


        AND a.username = #{username}


        AND a.notice_flag = #{noticeFlag}


        AND a.update_by = #{updateBy}


        AND a.serial_id like concat('%', #{serialId}, '%')


        AND a.tx_id like concat('%', #{txId}, '%')


        AND a.status = #{status}


        AND a.coin like concat('%',#{coin},'%')


        AND u.is_test = #{isTest}


        AND a.type = #{type}




                AND a.order_type = #{orderType}


                AND (a.order_type = #{orderType} or a.order_type is null)




        AND find_in_set(#{adminParentIds},a.admin_parent_ids)



            AND a.create_time >= #{params.beginTime}


            AND a.create_time <= #{params.endTime}


            AND a.operate_time >= #{params.startOperateTime}


            AND a.operate_time <= #{params.endOperateTime}


            AND u.is_test = #{params.isTest}


            AND u.is_test = #{params.isTest}


            AND a.amount >= #{params.minMoney}


            AND a.amount <= #{params.maxMoney}



GROUP BY a.serial_id

select IFNULL(SUM(amount),0) AS amount
from t_app_recharge a
LEFT JOIN t_app_user u ON a.user_id = u.user_id
where 1=1

    AND a.id= #{id}


    AND a.user_id = #{userId}


    AND a.address = #{address}



    AND a.username = #{username}



    AND a.update_by = #{updateBy}



    AND a.serial_id like concat('%', #{serialId}, '%')


    AND a.tx_id like concat('%', #{txId}, '%')


    AND a.status = #{status}


    AND a.type = #{type}



        AND u.is_test = #{params.isTest}


        AND a.amount >= #{params.minMoney}


        AND a.amount <= #{params.maxMoney}


        AND a.update_time >= #{params.beginTime}


        AND a.update_time <= #{params.endTime}

mapper.update:
update t_app_recharge
create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, user_id = #{userId}, username = #{username}, amount = #{amount}, bonus = #{bonus}, status = #{status}, serial_id = #{serialId}, tx_id = #{txId}, type = #{type}, search_value = #{searchValue}, address = #{address}, app_parent_ids = #{appParentIds}, admin_parent_ids = #{adminParentIds}, coin = #{coin}, to_address = #{toAddress}, block_time = #{blockTime}, host = #{host}, real_amount = #{realAmount}, file_name = #{fileName}, recharge_remark = #{rechargeRemark}, notice_flag = #{noticeFlag}, order_type = #{orderType},
where id = #{id}

update t_app_recharge
status = #{status}, update_time = sysdate(), update_by =#{updateBy}, remark=#{remark}, real_amount=#{realAmount}, amount=#{amount}, recharge_remark=#{rechargeRemark}
where id = #{id}

mapper.select:
SELECT DATE_FORMAT(w.create_time,'%Y-%m-%d') as createTime, IFNULL(SUM(t.u_amount),0) as recharge FROM t_app_recharge w LEFT JOIN t_app_user u ON w.user_id = u.user_id LEFT JOIN t_app_wallet_record t ON t.serial_id = w.serial_id WHERE u.is_test = 0 AND w.`status` = 1 AND (w.order_type = 1 OR w.order_type IS NULL)
AND find_in_set(#{parentId},w.admin_parent_ids)
AND DATE_SUB(CURDATE(), INTERVAL 7 DAY) < DATE(w.create_time) AND w.user_id IN (SELECT u1.user_id FROM t_app_user u1) GROUP BY createTime;

SELECT DATE_FORMAT(w.create_time,'%Y-%m-%d') as createTime, IFNULL(SUM(w.amount),0) as recharge FROM t_app_recharge w LEFT JOIN t_app_user u ON w.user_id = u.user_id WHERE u.is_test = 0 AND w.`status` = 2 AND (w.order_type = 1 OR w.order_type IS NULL)
AND find_in_set(#{parentId},w.admin_parent_ids)
AND DATE_SUB(CURDATE(), INTERVAL 7 DAY) < DATE(w.create_time) AND w.user_id IN (SELECT u1.user_id FROM t_app_user u1) GROUP BY createTime;
