mapper.select:
select a.user_id AS userId, a.login_name AS loginName, a.login_password AS loginPassword, a.phone, a.is_test AS isTest, a.address,
a.wallet_type AS walletType, a.status, a.totle_amont AS totleAmont, a.buff, a.app_parent_ids AS appParentIds, a.active_code AS activeCode, a.register_ip AS registerIp,
a.host, a.admin_parent_ids AS adminParentIds, a.email, a.level, a.is_freeze AS isFreeze, a.create_by AS createBy, a.create_time AS createTime, a.update_by AS updateBy,
a.update_time AS updateTime, a.remark, a.search_value AS searchValue, a.is_black AS isBlack, a.recharge_amont AS rechargeAmont,
(
SELECT
GROUP_CONCAT( s.user_name ORDER BY FIND_IN_SET(s.user_id,a.admin_parent_ids) )
FROM
sys_user s
WHERE
FIND_IN_SET(s.user_id,a.admin_parent_ids)
) AS adminParentNames,
(
SELECT
GROUP_CONCAT(t.login_name ORDER BY FIND_IN_SET(t.user_id,a.app_parent_ids) )
from
t_app_user t
where FIND_IN_SET(t.user_id,a.app_parent_ids)
) AS appParentNames ,
(
select  IFNULL(b.win_num, 0)
from t_app_user_detail b where  a.user_id = b.user_id
) AS win_num ,
(
select  IFNULL(c.lose_num, 0)
from t_app_user_detail c where  a.user_id = c.user_id
) AS lose_num


from t_app_user a

     and a.user_id =#{userId}
     and a.login_name like concat('%', #{loginName}, '%')
     and a.login_password = #{loginPassword}
     and a.phone = #{phone}
     and a.is_test = #{isTest}
     and a.address = #{address}
     and a.wallet_type = #{walletType}
     and a.status = #{status}
     and a.totle_amont = #{totleAmont}
     and a.buff = #{buff}
     and find_in_set(#{appParentIds},a.app_parent_ids)
     and a.active_code = #{activeCode}
     and a.register_ip = #{registerIp}
     and a.host = #{host}
     and find_in_set(#{adminParentIds},a.admin_parent_ids)
     and a.email = #{email}
     and a.level = #{level}
     and a.is_freeze = #{isFreeze}
     and a.search_value = #{searchValue}

select a.user_id AS userId, a.login_name AS loginName, a.login_password AS loginPassword, a.phone, a.is_test AS isTest, a.address,
a.wallet_type AS walletType, a.status, a.totle_amont AS totleAmont, a.buff, a.app_parent_ids AS appParentIds, a.active_code AS activeCode, a.register_ip AS registerIp,
a.host, a.admin_parent_ids AS adminParentIds, a.email, a.level, a.is_freeze AS isFreeze, a.create_by AS createBy, a.create_time AS createTime, a.update_by AS updateBy,
a.update_time AS updateTime, a.remark, a.search_value AS searchValue, a.recharge_amont AS rechargeAmont,
(
        SELECT
        GROUP_CONCAT( s.user_name )
        FROM
        sys_user s
        WHERE
        FIND_IN_SET(s.user_id,a.admin_parent_ids)
    ) AS adminParentNames,
    (
        SELECT
        GROUP_CONCAT(t.login_name)
        from
        t_app_user t
        where FIND_IN_SET(t.user_id,a.app_parent_ids)
    ) AS appParentNames
from t_app_user a

    not exists  (
    SELECT user_id from  t_ming_product_user pu
    LEFT JOIN t_ming_product p ON p.id = pu.product_id
    WHERE a.user_id =pu.app_user_id
     and pu.product_id = #{productId}
     )
     and a.user_id =#{userId}
     and a.login_name like concat('%', #{loginName}, '%')
     and a.login_password = #{loginPassword}
     and a.phone = #{phone}
     and a.is_test = #{isTest}
     and a.address = #{address}
     and a.wallet_type = #{walletType}
     and a.status = #{status}
     and a.totle_amont = #{totleAmont}
     and a.buff = #{buff}
     and find_in_set(#{appParentIds},a.app_parent_ids)
     and a.active_code = #{activeCode}
     and a.register_ip = #{registerIp}
     and a.host = #{host}
     and find_in_set(#{adminParentIds},a.admin_parent_ids)
     and a.email = #{email}
     and a.level = #{level}
     and a.is_freeze = #{isFreeze}
     and a.search_value = #{searchValue}

mapper.update:
update t_app_user
login_name = #{loginName}, login_password = #{loginPassword}, phone = #{phone}, is_test = #{isTest}, address = #{address}, wallet_type = #{walletType}, status = #{status}, totle_amont = #{totleAmont}, buff = #{buff}, app_parent_ids = #{appParentIds}, active_code = #{activeCode}, register_ip = #{registerIp}, host = #{host}, admin_parent_ids = #{adminParentIds}, email = #{email}, level = #{level}, is_freeze = #{isFreeze}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, search_value = #{searchValue}, is_black = #{isBlack}, recharge_amont = #{rechargeAmont}, tx_status = #{txStatus},
where user_id = #{userId}
