mapper.select:
select
r.id,
r.amount,
r.create_by createBy,
r.create_time createTime,
r.update_by updateBy,
r.update_time updateTime,
r.remark,
r.user_id userId,
r.search_value searchValue,
r.before_amount beforeAmount,
r.after_amount afterAmount,
r.serial_id serialId,
r.type,
r.symbol,
r.admin_parent_ids adminParentIds,
r.u_amount uAmount,
u.is_test isTest
from t_app_wallet_record r left  join  t_app_user u on r.user_id=u.user_id

    and r.amount = #{amount}
    and r.user_id = #{userId}
    and r.search_value = #{searchValue}
    and r.before_amount = #{beforeAmount}
    and r.after_amount = #{afterAmount}
    and r.serial_id = #{serialId}
    and r.type = #{type}
    and r.symbol = #{symbol}

        AND r.create_time >= #{startTime}


        AND r.create_time <= #{endTime}

    and r.amount <= #{maxAmount}
    and u.is_test= #{isTest}
    and r.amount >= #{minAmount}
    and find_in_set(#{adminParentIds},r.admin_parent_ids)
    and r.u_amount = #{uAmount}


            AND date_format(r.operate_time,'%Y-%m-%d %H:%i:%s') >= date_format(#{params.startOperateTime},'%Y-%m-%d %H:%i:%s')


            AND date_format(r.operate_time,'%Y-%m-%d %H:%i:%s') <= date_format(#{params.endOperateTime},'%Y-%m-%d %H:%i:%s')


            AND date_format(r.create_time,'%Y-%m-%d %H:%i:%s') >= date_format(#{params.beginTime},'%Y-%m-%d %H:%i:%s')


            AND date_format(r.create_time,'%Y-%m-%d %H:%i:%s') <= date_format(#{params.endTime},'%Y-%m-%d %H:%i:%s')



ORDER  BY  r.create_time DESC

mapper.update:
update t_app_wallet_record
amount = #{amount}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, user_id = #{userId}, search_value = #{searchValue}, before_amount = #{beforeAmount}, after_amount = #{afterAmount}, serial_id = #{serialId}, type = #{type}, symbol = #{symbol}, admin_parent_ids = #{adminParentIds}, u_amount = #{uAmount},
where id = #{id}

mapper.select:
SELECT SUM( IF( t.type = 1 && (SELECT r.status FROM t_app_recharge r WHERE t.serial_id=r.serial_id )=1, t.u_amount, 0 )) AS totalRechargeAmount, SUM( IF( t.type = 2 && (SELECT w.status FROM t_withdraw w WHERE t.serial_id=w.serial_id )=1, t.u_amount, 0 )) AS totalWithdrawAmount, SUM( IF( t.type = 3, t.u_amount, 0 )) AS totalWingAmount, SUM( IF( t.type = 50, t.u_amount, 0 )) AS totalSubBousAmount, SUM( IF( t.type = 4, t.u_amount, 0 )) AS totalSubAmount, SUM( IF( t.type = 51, t.u_amount, 0 )) AS totalAddAmount, SUM( IF( t.type = 40, t.u_amount, 0 )) AS totalCollectAmount, SUM( IF( t.type = 5, t.u_amount, 0 )) AS betAmount, SUM( IF( t.type = 9, t.u_amount, 0 )) AS rewardAmount, SUM( IF( t.type = 28 || t.type = 29, t.u_amount, 0 )) AS totalContractAmount, SUM( IF( t.type = 6, t.u_amount, 0 )) AS totalMattersAmount FROM t_app_wallet_record t ,t_app_user u
t.user_id = u.user_id and u.is_test = 0
AND t.create_time >= #{params.beginTime}
AND t.create_time <= #{params.endTime}

SELECT c.agencyId, c.agencyUserName, IF( c.user_id IS NOT NULL, group_concat( c.user_id SEPARATOR ',' ), '' ) AS appAllUserId, sum( c.recharge ) AS recharge, sum( c.withdraw ) AS withdraw, sum( c.sendBonus ) AS sendBonus, sum( c.subBonus ) AS subBonus, sum( c.btcManualScoring ) AS btcManualScoring, sum( c.ethManualScoring ) AS ethManualScoring, sum( c.subAmount ) AS subAmount, sum( c.sendAmount ) AS sendAmount, sum( c.btcManualSubdivision ) AS btcManualSubdivision, sum( c.ethManualSubdivision ) AS ethManualSubdivision FROM ( SELECT a.user_id AS agencyId, a.user_name AS agencyUserName, u.user_id, sum( IF ( r.type = 1, r.amount, 0 ) ) AS recharge, sum( IF ( r.type = 2, r.amount, 0 ) ) AS withdraw, sum( IF ( r.type = 3, r.amount, 0 ) ) AS sendBonus, sum( IF ( r.type = 50, r.amount, 0 ) ) AS subBonus, sum( IF ( r.type = 16, r.amount, 0 ) ) AS btcManualScoring, sum( IF ( r.type = 18, r.amount, 0 ) ) AS ethManualScoring, sum( IF ( r.type = 4, r.amount, 0 ) ) AS subAmount, sum( IF ( r.type = 51, r.amount, 0 ) ) AS sendAmount, sum( IF ( r.type = 17, r.amount, 0 ) ) AS btcManualSubdivision, sum( IF ( r.type = 19, r.amount, 0 ) ) AS ethManualSubdivision FROM sys_user a LEFT JOIN t_app_user u ON FIND_IN_SET( a.user_id, u.admin_parent_ids ) LEFT JOIN t_app_wallet_record r ON r.user_id = u.user_id WHERE a.user_type != 0 and a.del_flag=0 and (a.parent_id = #{adminParentIds} or a.user_id = #{adminParentIds}) GROUP BY a.user_id,u.user_id ) c
and c.user_id = #{userId}
GROUP BY c.agencyId
