mapper.update:
update t_withdraw
create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, user_id = #{userId}, username = #{username}, address = #{address}, amount = #{amount}, status = #{status}, serial_id = #{serialId}, search_value = #{searchValue}, from_addr = #{fromAddr}, type = #{type}, coin = #{coin}, ratio = #{ratio}, fee = #{fee}, withdraw_id = #{withdrawId}, host = #{host}, real_amount = #{realAmount}, to_adress = #{toAdress}, admin_parent_ids = #{adminParentIds}, notice_flag = #{noticeFlag}, with_draw_remark = #{withDrawRemark}, bank_name = #{bankName}, bank_user_name = #{bankUserName}, bank_branch = #{bankBranch}, fixed_fee = #{fixedFee}, order_type = #{orderType}, exchange_rate = #{exchangeRate}, receipt_amount = #{receiptAmount}, receipt_real_amount = #{receiptRealAmount}, receipt_coin = #{receiptCoin},
where id = #{id}

mapper.select:
SELECT DATE_FORMAT(w.create_time,'%Y-%m-%d') as createTime, IFNULL(SUM(t.u_amount),0) as withdraw FROM t_withdraw w LEFT JOIN t_app_user u ON w.user_id = u.user_id LEFT JOIN t_app_wallet_record t ON t.serial_id = w.serial_id WHERE u.is_test = 0 AND w.`status` = 1 AND (w.order_type = 1 OR w.order_type IS NULL)
AND find_in_set(#{parentId},w.admin_parent_ids)
AND DATE_SUB(CURDATE(), INTERVAL 7 DAY) < DATE(w.create_time) AND w.user_id IN (SELECT u1.user_id FROM t_app_user u1) GROUP BY createTime;

SELECT DATE_FORMAT(w.create_time,'%Y-%m-%d') as createTime, IFNULL(SUM(w.amount),0) as withdraw FROM t_withdraw w LEFT JOIN t_app_user u ON w.user_id = u.user_id WHERE u.is_test = 0 AND w.`status` = 2 AND (w.order_type = 1 OR w.order_type IS NULL)
AND find_in_set(#{parentId},w.admin_parent_ids)
AND DATE_SUB(CURDATE(), INTERVAL 7 DAY) < DATE(w.create_time) AND w.user_id IN (SELECT u1.user_id FROM t_app_user u1) GROUP BY createTime;
